var brand = document.getElementById("navBrand"), header = document.getElementById("header"), navCenter = document.getElementById("navCenter"), navDesc = document.getElementById("navDesc"), sect1 = document.getElementById("sect1"), sect2 = document.getElementById("sect2"), sect1phrase = document.getElementById("sect1phrase"), sect2phrase = document.getElementById("sect2phrase"), centralSymbol = document.getElementById("centralSymbol"), fadeObj = document.querySelectorAll(".fade-in-last"), screen = window.matchMedia("(min-width: 768px)");

function pageLoad() {
    setTimeout(function() {
        moveUpHalf(brand, function() {
            setTimeout(function() {
                fadeIn(navDesc, function() {
                    setTimeout(function() {
                        sectionAnim(screen, function() {
                            screen.addListener(sectionAnim), setTimeout(function() {
                                fadeIn(sect1phrase, function() {
                                    setTimeout(function() {
                                        fadeIn(centralSymbol, function() {
                                            setTimeout(function() {
                                                fadeIn(sect2phrase, function() {
                                                    setTimeout(function() {
                                                        fadeInArr(fadeObj, function() {
                                                            console.log("t");
                                                        });
                                                    }, 500);
                                                });
                                            }, 300);
                                        });
                                    }, 300);
                                });
                            }, 2e3);
                        });
                    }, 500);
                });
            }, 500);
        });
    }, 500);
}

function fadeIn(e, t) {
    var n = .1, l = setInterval(function() {
        1 <= n ? clearInterval(l) : (e.style.opacity = n, e.style.filter = "alpha(opacity=" + 100 * n + ")", 
        n += .1 * n);
    }, 10);
    t();
}

function fadeInArr(t, e) {
    var n = .1, l = setInterval(function() {
        for (var e = 0; e < t.length; e++) 2 <= n ? clearInterval(l) : (t[e].style.opacity = n, 
        t[e].style.filter = "alpha(opacity=" + 100 * n + ")", n += .1 * n);
    }, 10);
    e();
}

function moveUpHalf(e, t) {
    var n = 50, l = setInterval(function() {
        0 === n ? (clearInterval(l), header.style.position = "fixed", e.style.position = "relative") : (n--, 
        e.style.top = n + "%", e.style.transform = "translate(-50%, -" + n + "%)");
    }, 1);
    t();
}

function moveUpFull(e) {
    var t = 100, n = setInterval(function() {
        0 === t ? clearInterval(n) : (t--, e.style.top = t + "%", e.style.display = "block");
    }, 1);
}

function moveDownFull(e) {
    var t = -100, n = setInterval(function() {
        0 === t ? clearInterval(n) : (t++, e.style.top = t + "%", e.style.display = "block");
    }, 1);
}

function moveLeftFull(e) {
    var t = 100, n = setInterval(function() {
        0 === t ? clearInterval(n) : (t--, e.style.left = t + "%", e.style.display = "block");
    }, 1);
}

function moveRightFull(e) {
    var t = -100, n = setInterval(function() {
        0 === t ? clearInterval(n) : (t++, e.style.left = t + "%", e.style.display = "block");
    }, 1);
}

function sectionAnim(e, t) {
    e.matches ? (sect1.style.left = "auto", sect2.style.left = "auto", navDesc.classList.remove("nav__desc--pos"), 
    moveDownFull(sect1), moveUpFull(sect2)) : (sect1.style.top = "auto", sect2.style.top = "auto", 
    navDesc.classList.add("nav__desc--pos"), moveRightFull(sect1), moveLeftFull(sect2)), 
    t();
}

window.onload = function() {
    pageLoad();
};