var brand = document.getElementById("headerBrand"), header = document.getElementById("header"), headerDesc = document.getElementById("headerDesc"), sect1 = document.getElementById("sect1"), sect2 = document.getElementById("sect2"), screen = window.matchMedia("(min-width: 768px)");

function pageLoad() {
    setTimeout(function() {
        moveUpHalf(brand);
    }, 500), setTimeout(function() {
        fadeIn(headerDesc);
    }, 800), setTimeout(function() {
        sectionAnimation(screen), screen.addListener(sectionAnimation);
    }, 1300);
}

function fadeIn(e) {
    var t = .1, n = setInterval(function() {
        1 <= t && clearInterval(n), e.style.opacity = t, e.style.filter = "alpha(opacity=" + 100 * t + ")", 
        t += .1 * t;
    }, 10);
}

function moveUpHalf(e) {
    var t = 50, n = setInterval(function() {
        0 === t ? (clearInterval(n), header.style.position = "fixed", e.style.position = "relative") : (t--, 
        e.style.top = t + "%", e.style.transform = "translate(-50%, -" + t + "%)");
    }, 1);
}

function moveUpFull(e) {
    var t = 100, n = setInterval(function() {
        0 === t ? clearInterval(n) : (t--, e.style.top = t + "%", e.style.display = "block");
    }, 1);
}

function moveDownFull(e) {
    var t = -100, n = setInterval(function() {
        0 === t ? clearInterval(n) : (t++, e.style.top = t + "%", e.style.display = "block");
    }, 1);
}

function moveLeftFull(e) {
    var t = 100, n = setInterval(function() {
        0 === t ? clearInterval(n) : (t--, e.style.left = t + "%", e.style.display = "block");
    }, 1);
}

function moveRightFull(e) {
    var t = -100, n = setInterval(function() {
        0 === t ? clearInterval(n) : (t++, e.style.left = t + "%", e.style.display = "block");
    }, 1);
}

function sectionAnimation(e) {
    e.matches ? (sect1.style.left = "unset", sect2.style.left = "unset", moveDownFull(sect1), 
    moveUpFull(sect2)) : (sect1.style.top = "unset", sect2.style.top = "unset", moveRightFull(sect1), 
    moveLeftFull(sect2));
}

window.onload = function() {
    pageLoad();
};